import { Fragment, useDispatch, useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { useEffect } from "react";

import { uiAction } from "./store/ui-slice";

function App() {
  const dispatch = useDispatch();
  const showCart = useSelector((state) => state.ui.cartRender);
  const cart = useSelector((state) => state.cart);
  const notif = useSelector((state) => state.ui.notif);

  useEffect(() => {
    const sendCartData = async () => {
      dispatch(
        uiAction.showNotif({
          status: "pending",
          title: "Sending..",
          message: "Sending cart data",
        })
      );
      const response = await fetch(
        "https://learn-react-hizdev-default-rtdb.asia-southeast1.firebasedatabase.app/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );

      if (!response.ok) {
        throw new Error("Sending cart data failed.");
      }

      dispatch(
        uiAction.showNotif({
          status: "success",
          title: "Success!",
          message: "Sent cart data successfully!",
        })
      );
    };

    sendCartData().catch((error) => {
      dispatch(
        uiAction.showNotif({
          status: "error",
          title: "Error!",
          message: "Sending cart data failed!",
        })
      );
    });
  }, [cart, dispatch]);

  return (
    <Fragment>
      {notif && <Notification status={notif.status} title={notif.title} message={notif.message} />}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
