import { createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
    name : "ui",
    initialState: {cartRender : false},
    reducers:{
        toggle(state){
            state.cartRender = !state.cartRender;
        }
    }
});


export const uiAction = uiSlice.actions;

export default uiSlice;
